package SyntaxClass2;

public class ModuluOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		float f=12.50f;
		float f1=2.5f;
		float result=f/f1;
		System.out.println(result); //float can hold up to 5-7 decimals
		
		double i=12;
		double y=7;
		double result1=i/y;
		double result2=i/y;
		
		System.out.println(result2);  //double can hold up to  10-15 decimals
		//modulus
		int d=4;
		int a=2;
		int mod=d%a;
				
        System.out.println(mod);
       // int num1=13 not possible to store decimals into int
        int num1=13;
        double num2=10;
        System.out.println(num1/num2);


		
	}

}
