package SyntaxClass2;

public class Operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 3;
		
		int toplam = a + b;
		int cik = a - b;
		int carpim = a * b;
		float bolum = a / b;
		int mod = a % b;
		
		System.out.println("Sayilarin Toplami :".toUpperCase() + toplam );
		System.out.println("Sayilarin farki :" + cik);
		System.out.println("Sayilarin Carpimi :" + carpim);
		System.out.println("Sayilarin Bolumu :" + bolum);
		System.out.println("Sayilarin Modulusu :" + mod);
		
	}

}
