package SyntaxClass2;

public class Strings1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Name = "Ali";
		String lName = "Pektemir";
		String City = "Tampa";
		String State = " Florida";
		
		
		
		System.out.println("My name is : " + Name);
		System.out.println("My last name is: " + lName);
		System.out.println("I live in city of: " + City );
		System.out.println("my phone is 354345354");
		
		System.out.println("and State" + State);
		
		System.out.println("My first name: " + Name.toUpperCase());
		System.out.println("My last name: " + lName.toLowerCase());
		System.out.println("City: " + City.toUpperCase() );
		
		System.out.println("##");
		System.out.println("My city name string length is: " + City.length() );
		
		System.out.println("My full name: " + City.charAt(0) );
		
	}

}
