package SyntaxClass7;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// how to print from 50 to 1
		//how to print odds number from 1 to 20
		
		int num = 50;
		while (num >=1) {
			System.out.println(num +" ");
			num--;
			
			
		}
		
	}

}
