package SyntaxClass8;

public class Mloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		for (int i=21 ; i<=50 ; i+=2)	  {
			
				System.out.println(i);
			
		}
			
		
		int total = 2;
		for(int k=1; k<4 ; k++) {
			total= total*k ;
			System.out.println(total);
			
		}
		
		
		
		
	}

}
